func getCategoryIcon(t models.TalentType) string {
	switch t {
	case models.Physical:
		return "bi bi-heart-pulse"
	case models.Mental:
		return "bi bi-brain"
	case models.Combat:
		return "bi bi-crosshair"
	case models.Miscellaneous:
		return "bi bi-puzzle"
	default:
		return "bi bi-star"
	}
}

func isSelected(inv *models.Investigator, talentName string) bool {
	for _, t := range inv.Talents {
		if t.Name == talentName {
			return true
		}
	}
	return false
}

func selectedClass(selected bool) string {
	if selected {
		return "talent-selected "
	}
	return ""
}

func recommendedClass(recommended bool) string {
	if recommended {
		return "talent-recommended "
	}
	return ""
}

func requiredClass(required bool) string {
	if required {
		return "talent-required "
	}
	return ""
}

func collapseClass(expanded bool) string {
	if expanded {
		return ""
	}
	return "collapsed"
}

func showClass(expanded bool) string {
	if expanded {
		return "show"
	}
	return ""
}

func countTalentsInCategory(talentType models.TalentType) int {
	count := 0
	for _, talent := range models.Talents {
		if talent.Type == talentType {
			count++
		}
	}
	return count
}

func isRecommended(inv *models.Investigator, talentName string) bool {
	for _, name := range inv.Archetype.SpecialArchetypeRules.RecommendedTalents {
		if name == talentName {
			return true
		}
	}
	return false
}

func isRequired(inv *models.Investigator, talentName string) bool {
	for _, name := range inv.Archetype.SpecialArchetypeRules.RequiredTalents {
		if name == talentName {
			return true
		}
	}
	return false
}

